import React, { useState } from 'react';
import './index.scss';


function App() {
  // let count = 0
  const [count, setCount] = React.useState(0)
  const onClick = () =>{
    setCount()
    setCount(count + 1)
  }

  return (
    <div className="App">
      <div>
        <h2>Счетчик:</h2>
        <h1>{count}</h1>
        <button className="minus">- Минус</button>
        <button onClick={onClick} className="plus">Плюс +</button>
      </div>
    </div>
  );
}

export default App;
